rule "Close water valve when leak detected"
when
    Item WaterLeakSensor changed to ON
then
    createTimer(now.plusMinutes(1), [ |
        if (WaterLeakSensor.state == ON) {
            sendCommand(WaterValve, OFF)
        }
    ])
end
